CREATE TABLE `BC_Spi_Routers` (  `ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增主键',  `RouterName` varchar(128) NOT NULL  COMMENT '路由器名称',  `RouterDesc` varchar(128) NOT NULL  COMMENT '路由器描述',  `RouterContent` varchar(128) NULL  COMMENT '路由器内容',  `AddTime` timestamp NULL DEFAULT NULL COMMENT '增加时间',  `UpdateTime` timestamp NULL DEFAULT NULL COMMENT '更新时间',  PRIMARY KEY (`ID`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='商家服务路由器配置表';