CREATE TABLE `BC_Spi_Service` (  `ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增主键',  `ServiceCode` int(11) NOT NULL COMMENT '服务代码',  `ServiceName` varchar(128) NOT NULL  COMMENT '服务名称',  `ServiceType` int(11) NOT NULL COMMENT '服务类型',  `Status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '服务状态：0 无效 1 有效',  `AddTime` timestamp NULL DEFAULT NULL COMMENT '增加时间',  `UpdateTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',  PRIMARY KEY (`ID`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='商家服务路由表';