CREATE TABLE `BC_Spi_ServiceProvider` (  `ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增主键',  `ServiceCode` int(11) NOT NULL COMMENT '服务代码',  `ProviderCode` int(11) NOT NULL COMMENT '渠道代码',  `ProviderName` varchar(128) NOT NULL  COMMENT '渠道名称',  `ProviderType` varchar(128) NOT NULL COMMENT '渠道类型',  `ProviderParams` varchar(128) NOT NULL COMMENT '渠道参数',  `ProviderStatus` tinyint(4) NOT NULL DEFAULT '1' COMMENT '渠道状态：0 无效 1 有效',  `AddTime` timestamp NOT NULL  COMMENT '增加时间',  `UpdateTime` timestamp NOT NULL   COMMENT '更新时间',  PRIMARY KEY (`ID`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='商家服务渠道表';